import React, { useState } from 'react';
// import axios from 'axios';

const LoginSignup = () => {

    const [signIn, toggle] = useState(true);

    const handleSignUp = async (event) => {
      // event.preventDefault();
      // const { name, email, password } = event.target.elements;
  
      // try {
      //   const response = await axios.post('/api/signup', {
      //     name: name.value,
      //     email: email.value,
      //     password: password.value,
      //   });
      //   console.log(response.data);
      // } catch (error) {
      //   console.error(error);
      // }
    };
  
    const handleSignIn = async (event) => {
      // event.preventDefault();
      // const { email, password } = event.target.elements;
  
      // try {
      //   const response = await axios.post('/api/signin', {
      //     email: email.value,
      //     password: password.value,
      //   });
      //   console.log(response.data);
      // } catch (error) {
      //   console.error(error);
      // }
    };
  return (
    <div className="container bg-white rounded-lg shadow-lg relative overflow-hidden w-full max-w-xl min-h-screen">
         <div className={`absolute top-0 h-full transition-all duration-600 ease-in-out left-0 w-1/2 ${signIn ? 'opacity-0 z-10' : 'transform translate-x-full opacity-100 z-20'}`}>
        <form onSubmit={handleSignUp} className="bg-white flex flex-col items-center justify-center p-12 h-full text-center">
          <h1 className="font-bold m-0">Create Account</h1>
          <input name="name" type="text" placeholder="Name" className="bg-gray-200 border-none p-3 m-2 w-full" />
          <input name="email" type="email" placeholder="Email" className="bg-gray-200 border-none p-3 m-2 w-full" />
          <input name="password" type="password" placeholder="Password" className="bg-gray-200 border-none p-3 m-2 w-full" />
          <button type="submit" className="rounded-2xl border border-red-600 bg-red-600 text-white text-xs font-bold py-3 px-12 tracking-wider uppercase transition-transform duration-75 ease-in transform hover:scale-95 focus:outline-none">
            Sign Up
          </button>
        </form>
      </div>

      <div className={`absolute top-0 h-full transition-all duration-600 ease-in-out left-0 w-1/2 z-20 ${signIn ? '' : 'transform translate-x-full'}`}>
        <form onSubmit={handleSignIn} className="bg-white flex flex-col items-center justify-center p-12 h-full text-center">
          <h1 className="font-bold m-0">Sign In</h1>
          <input name="email" type="email" placeholder="Email" className="bg-gray-200 border-none p-3 m-2 w-full" />
          <input name="password" type="password" placeholder="Password" className="bg-gray-200 border-none p-3 m-2 w-full" />
          <a href="#" className="text-gray-800 text-sm no-underline m-3">Forgot your password?</a>
          <button type="submit" className="rounded-2xl border border-red-600 bg-red-600 text-white text-xs font-bold py-3 px-12 tracking-wider uppercase transition-transform duration-75 ease-in transform hover:scale-95 focus:outline-none">
            Sign In
          </button>
        </form>
      </div>

      <div className={`absolute top-0 left-1/2 w-1/2 h-full overflow-hidden transition-transform duration-600 ease-in-out z-50 ${signIn ? '' : 'transform -translate-x-full'}`}>
        <div className={`bg-gradient-to-r from-red-600 to-red-500 bg-cover bg-center text-white absolute left-full h-full w-full transform translate-x-0 transition-transform duration-600 ease-in-out ${signIn ? '' : 'transform -translate-x-1/2'}`}>
          <div className="absolute flex items-center justify-center flex-col p-10 text-center top-0 h-full w-1/2 transform transition-transform duration-600 ease-in-out">
            <h1 className="font-bold">Welcome Back!</h1>
            <p className="text-sm font-light leading-5 tracking-wider my-5">To keep connected with us please login with your personal info</p>
            <button onClick={() => toggle(true)} className="rounded-2xl border border-white bg-transparent text-white text-xs font-bold py-3 px-12 tracking-wider uppercase transition-transform duration-75 ease-in transform hover:scale-95 focus:outline-none">
              Sign In
            </button>
          </div>
          <div className="absolute flex items-center justify-center flex-col p-10 text-center top-0 h-full w-1/2 right-0 transform translate-x-0 transition-transform duration-600 ease-in-out">
            <h1 className="font-bold">Hello, Friend!</h1>
            <p className="text-sm font-light leading-5 tracking-wider my-5">Enter your personal details and start your journey with us</p>
            <button onClick={() => toggle(false)} className="rounded-2xl border border-white bg-transparent text-white text-xs font-bold py-3 px-12 tracking-wider uppercase transition-transform duration-75 ease-in transform hover:scale-95 focus:outline-none">
              Sign Up
            </button>
          </div>
        </div>
      </div>
      
    </div>
  )
}

export default LoginSignup
