import styled from 'styled-components';

export const Container = styled.div`
  @apply bg-white rounded-lg shadow-lg relative overflow-hidden w-full max-w-xl min-h-screen;
`;

export const SignUpContainer = styled.div`
  @apply absolute top-0 h-full transition-all duration-600 ease-in-out left-0 w-1/2 opacity-0 z-10;
  ${({ signinIn }) =>
    signinIn !== true ? `transform: translateX(100%); opacity: 1; z-index: 20;` : null}
`;

export const SignInContainer = styled.div`
  @apply absolute top-0 h-full transition-all duration-600 ease-in-out left-0 w-1/2 z-20;
  ${({ signinIn }) => (signinIn !== true ? `transform: translateX(100%);` : null)}
`;

export const Form = styled.form`
  @apply bg-white flex flex-col items-center justify-center p-12 h-full text-center;
`;

export const Title = styled.h1`
  @apply font-bold m-0;
`;

export const Input = styled.input`
  @apply bg-gray-200 border-none p-3 m-2 w-full;
`;

export const Button = styled.button`
  @apply rounded-2xl border border-red-600 bg-red-600 text-white text-xs font-bold py-3 px-12 tracking-wider uppercase transition-transform duration-75 ease-in transform;
  &:active {
    transform: scale(0.95);
  }
  &:focus {
    outline: none;
  }
`;

export const GhostButton = styled(Button)`
  @apply bg-transparent border-white;
`;

export const Anchor = styled.a`
  @apply text-gray-800 text-sm no-underline m-3;
`;

export const OverlayContainer = styled.div`
  @apply absolute top-0 left-1/2 w-1/2 h-full overflow-hidden transition-transform duration-600 ease-in-out z-50;
  ${({ signinIn }) =>
    signinIn !== true ? `transform: translateX(-100%);` : null}
`;

export const Overlay = styled.div`
  @apply bg-gradient-to-r from-red-600 to-red-500 bg-cover bg-center text-white absolute left-full h-full w-full transform translate-x-0 transition-transform duration-600 ease-in-out;
  ${({ signinIn }) => (signinIn !== true ? `transform: translateX(-50%);` : null)}
`;

export const OverlayPanel = styled.div`
  @apply absolute flex items-center justify-center flex-col p-10 text-center top-0 h-full w-1/2 transform transition-transform duration-600 ease-in-out;
`;

export const LeftOverlayPanel = styled(OverlayPanel)`
  @apply transform -translate-x-full;
  ${({ signinIn }) => signinIn !== true ? `transform: translate-x-0;` : null}
`;

export const RightOverlayPanel = styled(OverlayPanel)`
  @apply right-0 transform translate-x-0;
  ${({ signinIn }) => signinIn !== true ? `transform: translate-x-full;` : null}
`;

export const Paragraph = styled.p`
  @apply text-sm font-light leading-5 tracking-wider my-5;
`;
